(3784, 116, 'block5_quote', '‘Elke werknemer, elk team of elke afdeling kan een idee indienen, of het nu gaat om een nieuw product, nieuwe software of een nieuw businessmodel.’'),
(3785, 116, '_block5_quote', 'field_5bc7070dc07f3'),
(3786, 116, 'block5_author', 'Susanne Hahn,<br> hoofd Lab 1886 Mercedes-Benz'),
(3787, 116, '_block5_author', 'field_5bc7071fc07f4'),
(3788, 116, 'block5_image', '115'),
