(4242, 126, 'block3_title', 'EQC'),
(4243, 126, '_block3_title', 'field_5bc7012c5fdf7'),
(4244, 126, 'block3_slider', '5'),
(4245, 126, '_block3_slider', 'field_5bc7013f89ad1'),
